import React, { Component } from 'react';
import './App.css';
import Fact from './Fact';
import 'tachyons';


class App extends Component {
  constructor(){
    super()
    this.state= {
      topic:'',
      number:'',
      fact:''
    }
  }
  getTrivia=()=>{
    let triviaNumber = document.getElementById('triviaInput').value;
    console.log(triviaNumber);
    this.setState({topic:'trivia'})
    this.setState({number: document.getElementById('triviaInput').value })
    fetch(`http://numbersapi.com/${triviaNumber}/trivia?json`)
    .then(resp => resp.json())
    .then(data => { return (
      this.setState({fact: data.text}),
      console.log(this.state.topic, this.state.number, this.state.fact)
    );

    })}
  render (){
    return(
      <div>
      <h1>Numbers</h1>
      <h2>Interesting facts about numbers</h2>
      <Fact
      topicToDisplay={this.state.topic}
      numberToDisplay={this.state.number}
      factToDisplay={this.state.fact}
      />
      <h1>Trivia
      <input className='pa3 b--green bg-lightest-blue' type='number' placeholder='enter number' id='triviaInput'/>
<p onClick={this.getTrivia}>Go</p>
      </h1>
      <h1>Math
      <input type='number' placeholder='enter number' id='mathInput'/>
      <p>
        onClick={this.getMath}
      </p>
      </h1>
      </div>
    )
  }

}

export default App;
